<style>
*{box-sizing:border-box}
:root{--bg:#072f3d;--bg-2:#0b3f52;--card:#0e5068;--muted:#9ec7d6;--text:#e9f6fb;--accent:#15b5ff;--good:#42d392;--danger:#ff6b6b;--border:rgba(255,255,255,.08);--shadow:0 10px 30px rgba(0,0,0,.35);--radius:14px;--radius-sm:10px;--radius-xs:7px}
.container{max-width:1100px;margin:0 auto;padding:20px 16px;margin-bottom:50px}
.tabs{display:flex;gap:12px}
.tab{position:relative;flex:1;text-align:center;padding:12px 14px;color:#333;font-weight:600;letter-spacing:.2px;border-radius:12px;cursor:pointer;user-select:none;transition:all .25s ease;border:0}
.tab:hover{color:#333;transform:translateY(-1px)}
.tab.active{color:#062632;background:linear-gradient(135deg,#4bd6ff,#28a7e6 60%,#20d1ff);box-shadow:0 6px 16px rgba(32,209,255,.35),inset 0 0 0 1px rgba(255,255,255,.25)}
.tab-panels{margin-top:16px}
.tab-panel{display:none;animation:fade .24s ease}
.tab-panel.active{display:block}
@keyframes fade{from{opacity:0;transform:translateY(6px)}to{opacity:1;transform:none}}
.grid{display:grid;grid-template-columns:repeat(2,minmax(0,1fr));gap:16px}
@media (max-width:820px){.grid{grid-template-columns:1fr}}
.card{background:linear-gradient(180deg,var(--card),#0a4254 140%);border:1px solid var(--border);border-radius:var(--radius);overflow:hidden;box-shadow:var(--shadow);position:relative;margin-bottom:15px}
.card-head{display:flex;align-items:start;gap:12px;padding:14px 14px 0 14px;color:#eee;font-size:14px}
.thumb{width:145px;height:100px;border-radius:10px;object-fit:cover;border:1px solid var(--border);background:#093845}
.title{color:var(--text);font-weight:700;line-height:1.25;font-size:16px}
.badge{margin-left:auto;font-size:12px;padding:6px 10px;border-radius:999px;color:#053042;background:linear-gradient(135deg,#b6ffcc,#54e58c);font-weight:700;position:absolute;top:14px;right:14px}
.card-body{padding:10px 14px 14px 14px;color:var(--text);display:flex}
.card-body > div{flex:1;display:flex;flex-direction:column-reverse;align-items:center;justify-content:center;font-size:13px}
.card-body2{padding:10px 14px 14px 14px;color:var(--text);font-size:14px}
.card-body2 > div{display:flex;justify-content:space-between;line-height:1.3}
.invest-title{font-size:16px;font-weight:bold;margin-bottom:15px;height:45px}
.meta-invest{border-bottom:1px solid #9ec7d6;padding-bottom:5px;margin-bottom:5px;color:#9ec7d6}
.meta-invest:last-child{border:0;margin:0;padding:0}
.meta-invest span:last-child{font-weight:500;color:#fff}
.meta{color:var(--muted);line-height:1.7}
.meta strong{color:#fff}
.card-foot{display:grid;gap:10px;grid-template-columns:1fr 1fr;padding:12px 14px 14px 14px}
.btn{border:1px solid var(--border);background:#3c9cff;color:#fff;padding:5px 10px;border-radius:5px;font-weight:600;cursor:pointer;font-size:13px;transition:.2s;box-shadow:0 2px 12px rgba(0,0,0,.15);text-align:center}
.btn:hover{transform:translateY(-1px);border-color:rgba(255,255,255,.25)}
.btn-primary{background:linear-gradient(135deg,#4bd6ff,#28a7e6 60%,#20d1ff);color:#063848;border-color:transparent}
.btn-primary:hover{filter:brightness(1.05)}
.tablewrap{background:var(--card);border:1px solid var(--border);border-radius:var(--radius);box-shadow:var(--shadow);overflow:auto}
table{border-collapse:separate;border-spacing:0;width:100%;min-width:760px;color:var(--text)}
thead th{position:sticky;top:0;background:#0b4860;color:#ccebf5;text-align:left;padding:12px;font-size:13px}
tbody td{padding:12px;border-top:1px solid var(--border);color:#e9f6fb}
.label{padding:6px 10px;border-radius:999px;font-size:12px;font-weight:700;position:absolute;top:14px;right:14px}
.label.success{background:rgba(66,211,146,.2);color:#5eedab;border:1px solid rgba(66,211,146,.5)}
.label.muted{background:rgba(255,255,255,.08);color:#d3e7ee;border:1px solid rgba(255,255,255,.15)}
.modal{position:fixed;inset:0;display:none;align-items:center;justify-content:center;background:rgba(0,0,0,.55);z-index:1000;padding:18px}
.modal.open{display:flex}
.modal-card{width:100%;max-width:520px;background:var(--card);border:1px solid var(--border);border-radius:18px;box-shadow:var(--shadow);overflow:hidden;color:var(--text)}
.modal-head{display:flex;align-items:center;justify-content:space-between;padding:14px 16px;border-bottom:1px solid var(--border)}
.modal-title{font-weight:bold}
.modal-body{padding:16px}
.modal-foot{padding:14px 16px;display:flex;gap:10px;justify-content:flex-end;border-top:1px solid var(--border)}
.close{cursor:pointer;font-size:22px;line-height:1;color:#cde1ea}
.input,select{width:100%;padding:12px 14px;border-radius:10px;border:1px solid var(--border);background:#0b4860;color:#fff;outline:none;box-shadow:inset 0 1px 0 rgba(255,255,255,.04)}
.modal-body label{margin-bottom:5px;font-size:14px;display:block}
.help{color:var(--muted);font-size:12px;margin-top:16px}
.text-muted{color:var(--muted)}
