<script>
(function(){
  var tabs = document.querySelectorAll('.tab');
  var panels = document.querySelectorAll('.tab-panel');
  tabs.forEach(function(t){
    t.addEventListener('click', function(){
      var targetId = t.getAttribute('aria-controls');
      tabs.forEach(x=>{x.classList.remove('active'); x.setAttribute('aria-selected','false');});
      panels.forEach(p=>p.classList.remove('active'));
      t.classList.add('active');
      t.setAttribute('aria-selected','true');
      document.getElementById(targetId).classList.add('active');
    });
  });
})();

/* ===== Modal (open/close) ===== */
var modal = document.getElementById('investModal');
function openModal(){ modal.classList.add('open'); modal.setAttribute('aria-hidden','false'); }
function closeModal(){ modal.classList.remove('open'); modal.setAttribute('aria-hidden','true'); }
document.addEventListener('click', function(e){
  if (e.target.matches('[data-close]') || e.target === modal) closeModal();
});
document.addEventListener('keydown', function(e){ if(e.key==='Escape') closeModal(); });

/* ===== Open invest with data ===== */
document.querySelectorAll('.js-open-invest').forEach(function(btn){
  btn.addEventListener('click', function(){
    var pid   = btn.getAttribute('data-project-id');
    var title = btn.getAttribute('data-project-title') || '';
    var minv  = btn.getAttribute('data-project-min') || 0;

    document.getElementById('investProjectId').value = pid;
    document.getElementById('investTitle').textContent = 'Đầu tư nhanh: ' + title;
    var input = document.getElementById('investAmount');
    input.value = '';
    input.min = minv;
    document.getElementById('investMin').textContent = '$' + minv;

    openModal();
  });
});

/* ===== AJAX submit (JSON) ===== */
document.getElementById('investForm').addEventListener('submit', function(e){
  e.preventDefault();
  var form = e.target;
  var data = new FormData(form);

  fetch(form.action, {
    method: 'POST',
    body: data,
    headers:{ 'X-Requested-With':'XMLHttpRequest' }
  }).then(function(r){ return r.json(); })
    .then(function(res){
      var msg = (res && res.data) ? res.data : 'Unknown';
      if (res && res.type == 1) {
        layer.msg(msg);
        setTimeout(function(){location.reload()},2000);
      } else {
        layer.msg(msg);
        return false;
      }
    }).catch(function(){
        layer.msg('lỗi');
        return false;
    });
});
</script>


